package job.jack.str;

public class ModifyCost {
    public static void main(String[] args) {
        String str1 = "srxhnghkxmssjxlyvrvnrnhkaqagucptyrfjczpfwnoylipweqvtzpaeulubpavspwkztbhyubmoalhn";
        String str2 = "";
        int ic = 16;
        int dc = 29;
        int rc = 28;

        System.out.println(minEditCost(str1, str2, ic, dc, rc));
    }

    /**
     * 计算将字符串1编辑为字符串2的最小代价
     * min edit cost
     * @param str1 string字符串 the string
     * @param str2 string字符串 the string
     * @param ic int整型 insert cost
     * @param dc int整型 delete cost
     * @param rc int整型 replace cost
     * @return int整型
     */
    public static int minEditCost (String str1, String str2, int ic, int dc, int rc) {
        // write code here
        int l1 = str1.length();
        int l2 = str2.length();
        int[][] dp = new int[l1 + 1][l2 + 1];

        // 初始化
        dp[0][0] = 0;
        for(int i = 1; i <= l2; i++){
            // 将空串编辑为str1的代价
            dp[0][i] = i * ic;
        }
        for(int i = 1; i <= l1; i++){
            // 将字符串str2编辑为空串
            dp[i][0] = i * dc;
        }
        for(int i = 1; i <= l1; i++){
            for(int j = 1; j <= l2; j++){
                // 判断最后一个字符是否相等
                if(str1.charAt(i - 1) - str2.charAt(j - 1) == 0){
                    dp[i][j] = dp[i - 1][j - 1];
                }else{
                    // 三种情况分开讨论
                    dp[i][j] = Math.min(dp[i][j - 1] + ic,
                            Math.min(dp[i - 1][j] + dc,
                                    dp[i - 1][j - 1] + rc));
                }
            }
        }

        return dp[l1][l2];
    }
}
